css:
@media(min-width: 769px) {
.shipping-line + small {
width: 50%
}
}
.top-bar {
background-color: #e8e1d5;
}
.header {
background-color: #e8e1d5;
}
.footer 
{
background-color: #e8e1d5;
}
body, p, .btn {
  font-family: 'Didact Gothic', sans-serif !important;
}
.shipping-calculator__services::before { content: "⚠️ Produto feito sob encomenda. Observe prazo de produção. Pronta-entrega somente na seção Ofertas."; border: 1px solid #000; border-radius: 2rem; padding: 1.5rem;
 font-size: 1.2rem; display: inherit;
 margin-bottom: 1rem 
}
.product__short-stock .fas.fa-forward, .product__short-stock strong, .product__short-stock strong:before, .product__short-stock strong:after {
font-size: .875rem !important
}
.product__short-stock strong:before {
content: 'Apenas '
}
.product__short-stock strong:after {
content: ' em estoque'
}
.product__short-stock {
font-size: 0px
}
.product__discount {
display: none !important;
}
.product-card__buy.fade {
  opacity: 1;
}
.product-card--small .product-card__buy {
  position: static;
  display: block;
}
.product__short-stock {
display: none !important;
}
.banner {
    width: 100% !important;
}
.footer .col-12.col-sm-6.col-lg:nth-child(3) {
  display: none !important;
}
.discount-applier.checkout__discount, 
.checkout__notes {
display: none !important;
}
.discount-applier.cart__discount { 
display: none !important; 
}
.order-info__toggle {
 display: none !important;
}
